void quickSort(vector<int>& arr, int l, int r){
    if(l >= r) return;
    int i = l, j = r;
    while(i < j){
      while(i < j && arr[j] >= arr[l]){j--;};
      while(i < j && arr[i] <= arr[l]){i++;};
      swap(arr[i], arr[j]);
    }
    swap(arr[i], arr[l]);
    quickSort(arr, l, i - 1);
    quickSort(arr, i + 1, r);
}
```

