```js
// wrong ×
// 只$on但不在组件销毁中销毁，导致eventBus 模块对象持有组件上下文，组件内存不释放，内存泄漏
import eventBus from '@/Common/vue/service/event/eventBus';
export default {
  created() {
    eventBus.$on('mylisntenr', ()=>{
      console.log('do something');
    });
  }
};

// wrong ×
