const maxSubArray = (nums: number[]): number => {
    let pre = 0, max = -Infinity
    for (const num of nums) { 
        if (pre < 0) {
            pre = 0
        }
        pre = num + pre 
        max = Math.max(pre, max)
    }
    return max
}
